import userEvent from "@testing-library/user-event";
import { useState } from "react";
import "./style.css";

const initialFacts = [
  {
    id: 1,
    text: "React is being developed by Meta (formerly facebook)",
    source: "https://opensource.fb.com/",
    category: "technology",
    votesInteresting: 24,
    votesMindblowing: 9,
    votesFalse: 4,
    createdIn: 2021,
  },
  {
    id: 2,
    text: "Millennial dads spend 3 times as much time with their kids than their fathers spent with them. In 1982, 43% of fathers had never changed a diaper. Today, that number is down to 3%",
    source:
      "https://www.mother.ly/parenting/millennial-dads-spend-more-time-with-their-kids",
    category: "society",
    votesInteresting: 11,
    votesMindblowing: 2,
    votesFalse: 0,
    createdIn: 2019,
  },
  {
    id: 3,
    text: "Lisbon is the capital of Portugal",
    source: "https://en.wikipedia.org/wiki/Lisbon",
    category: "society",
    votesInteresting: 8,
    votesMindblowing: 3,
    votesFalse: 1,
    createdIn: 2015,
  },
];

function App() {
  const [showForm, setShowForm] = useState(false);

  return (
    <>
      <Header showForm={showForm} setShowForm={setShowForm} />
      {showForm ? <NewFactForm /> : null}

      <main className="main">
        <CategoryFilter />
        <FactList />
      </main>
    </>
  );
}

function Header({ showForm, setShowForm }) {
  const appTitle = "React Project";
  return (
    <header className="header">
      <div className="logo">
        <img src="./logo.png" alt="logo" />
        <h1>{appTitle}</h1>
      </div>
      <button
        className="btn btn-large btn-open"
        onClick={() => setShowForm((show) => !show)}
      >
        {showForm ? "CLOSE" : "Share a Fact"}
      </button>
    </header>
  );
}

const CATEGORIES = [
  { name: "technology", color: "#3b82f6" },
  { name: "science", color: "#16a34a" },
  { name: "finance", color: "#ef4444" },
  { name: "society", color: "#eab308" },
  { name: "entertainment", color: "#db2777" },
  { name: "health", color: "#14b8a6" },
  { name: "history", color: "#f97316" },
  { name: "news", color: "#8b5cf6" },
];

function NewFactForm() {
  {
    const [text, setText] = useState("");
    const [source, setSource] = useState("");
    const [category, setCategory] = useState("");

    function isValidHttpUrl(string) {
      let url;
      try {
        url = new URL(string);
      } catch (_) {
        return false;
      }
      return url.protocol === "http:" || url.protocol === "https:";
    }

    function handleSubmit(e) {
      //browser·Äõ·Ä≤·Ä∑ default loading ·ÄÄ·Ä≠·ÄØ ·Äõ·Äï·Ä∫·Äï·ÄÖ·Ä∫·Äõ·Äî·Ä∫
      e.preventDefault(); //loading false
      // console.log(text, source, category);

      //User ·Äë·Ää·Ä∑·Ä∫·Äú·Ä≠·ÄØ·ÄÄ·Ä∫·Äê·Ä≤·Ä∑ data·Äê·ÄΩ·Ä± Form·Äë·Ä≤·Äô·Äæ·Ä¨ ·Äê·ÄÄ·Äö·Ä∫ ·Äï·Ä´/·Äô·Äï·Ä´ ·ÄÖ·ÄÖ·Ä∫·Äï·Ä±·Ä∏·Äõ·Äô·Äæ·Ä¨.. ·Äï·Ä´·Äê·Äö·Ä∫·ÄÜ·Ä≠·ÄØ·Äõ·ÄÑ·Ä∫ Fact ·Ä°·Äû·ÄÖ·Ä∫·ÄÜ·Ä±·Ä¨·ÄÄ·Ä∫
      if (text && isValidHttpUrl(source) && category && textLength <= 200) {
        //Fact ·Ä°·Äû·ÄÖ·Ä∫·ÄÄ·Ä≠·ÄØ Fact Object ·Ä°·Äñ·Äº·ÄÖ·Ä∫·Äï·Äº·Ä±·Ä¨·ÄÑ·Ä∫·Ä∏·Äõ·Äî·Ä∫
        const newFact = {
          id: Math.round(Math.random() * 1000000),
          text: text,
          source: source,
          category: category,
          votesInteresting: 0,
          votesMindblowing: 0,
          votesFalse: 0,
          createdIn: new Date().getFullYear(),
        };

        //Fact Object ·ÄÄ·Ä≠·ÄØ UI·Äô·Äæ·Ä¨ ·Äï·Äº·Äï·Ä±·Ä∏·Äõ·Äî·Ä∫.... Fact State ·Ä°·Äû·ÄÖ·Ä∫·Äê·ÄÖ·Ä∫·ÄÅ·ÄØ·Äõ·Ä±·Ä∏·Äï·Ä±·Ä∏·Äõ·Äî·Ä∫
        setFacts((currentFacts) => [newFact, ...currentFacts]);
        //Form·Äë·Ä≤·ÄÄ Data·Äê·ÄΩ·Ä±·ÄÄ·Ä≠·ÄØ ·Ä°·ÄÄ·ÄØ·Äî·Ä∫·Äï·Äº·Äî·Ä∫·Äñ·Äª·ÄÄ·Ä∫·Äõ·Äî·Ä∫
        setText("");
        setSource("");
        setCategory("");
        //Form ·ÄÄ·Ä≠·ÄØ ·Äï·Äº·Äî·Ä∫·Äï·Ä≠·Äê·Ä∫·Äõ·Äî·Ä∫
      }
    }

    const textLength = text.length;

    // function isValidHttpUrl(string) {
    //   let url;
    //   try {
    //     url = new URL(string);
    //   } catch (_) {
    //     return false;
    //   }
    //   return url.protocol === "http:" || url.protocol === "https:";
    // }

    // function handleSubmit(e) {
    //   e.preventDefault();
    //   if (text && category && isValidHttpUrl(source) && textLength <= 200) {
    //     const newFact = {
    //       id: Math.round(Math.random() * 1000000),
    //       text,
    //       source,
    //       category,
    //       votesInteresting: 0,
    //       votesMindblowing: 0,
    //       votesFalse: 0,
    //       createdIn: new Date().getFullYear(),
    //     };
    //   }
    // }

    return (
      <form className="fact-form" onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Share A Fact With The Words...."
          value={text}
          onChange={(e) => setText(e.target.value)}
        />
        <span className="counting-words">{200 - textLength}</span>
        <input
          type="text"
          placeholder="Trustworthy Source...."
          value={source}
          onChange={(e) => setSource(e.target.value)}
        />
        <select
          name=""
          id=""
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        >
          <option value="">Chose catagory:</option>
          {CATEGORIES.map((cat) => (
            <option key={cat.name} value={cat.name}>
              {cat.name.toUpperCase()}
            </option>
          ))}
        </select>
        <button className="btn btn-large">Post</button>
      </form>
    );
  }

  function CategoryFilter() {
    return (
      <aside>
        <ul>
          <li className="categroy ">
            <button className="btn btn-all-category">All</button>
          </li>
          {CATEGORIES.map((cat) => (
            <li key={cat.name} className="categroy">
              <button
                className="btn btn-category "
                style={{ backgroundColor: cat.color }}
              >
                {cat.name}
              </button>
            </li>
          ))}
        </ul>
      </aside>
    );
  }

  function FactList({ facts }) {
    return (
      <section>
        <ul className="fact-list">
          {facts.map((fact) => (
            <Fact key={fact.id} fact={fact} />
          ))}
        </ul>
        {/* <p>There are {facts.length} Facts.</p> */}
      </section>
    );
  }
  function Fact(props) {
    const { fact } = props;
    // console.log(fact);
    return (
      <li className="fact">
        <p>
          {fact.text}
          <a className="source" href={fact.source} target="_blank">
            (Source)
          </a>
        </p>
        <span
          className="tag"
          style={{
            backgroundColor: CATEGORIES.find((cat) => cat.name == fact.category)
              .color,
          }}
        >
          {fact.category}
        </span>
        <div className="vote-buttons">
          <button>üëç {fact.votesInteresting}</button>
          <button>ü§Ø {fact.votesMindblowing}</button>
          <button>‚õî {fact.votesFalse}</button>
        </div>
      </li>
    );
  }
}
export default App;
